import React, { useEffect, useState } from 'react';
import { CircularProgress, Typography } from '@mui/material';
import PageContainer from './PageContainer';
import PokemonDetails from './PokemonDetails';

function PokemonPage() {
  const [pokemon, setPokemon] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch("https://pokeapi.co/api/v2/pokemon/ditto")
      .then(response => response.json())
      .then(data => {
        setPokemon(data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
        setError('Failed to fetch data');
        setLoading(false);
      });
  }, []);

  if (loading) {
    return (
      <PageContainer title="Loading..." description="Loading Pokémon details...">
        <CircularProgress />
      </PageContainer>
    );
  }

  if (error) {
    return (
      <PageContainer title="Error" description="Unable to load Pokémon details">
        <Typography color="error">{error}</Typography>
      </PageContainer>
    );
  }

  return (
    <PageContainer title={pokemon.name} description={`Details about ${pokemon.name}`}>
      <PokemonDetails data={pokemon} />
    </PageContainer>
  );
}

export default PokemonPage;
