import { Backdrop, CircularProgress } from "@mui/material";
import React, { useEffect, useState } from "react";
import { Outlet } from "react-router-dom";
import Header from "./Header";

function MainLayout() {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simula carga de datos o espera a la carga real desde una API
    setTimeout(() => {
      setLoading(false);
    }, 2000);
  }, []);

  return (
    <>
      {!loading ? (
        <>
          <Header />
          <Outlet /> {/* Asegúrate que este Outlet carga componentes de Pokémon */}
        </>
      ) : (
        <Backdrop
          sx={{ color: "#fff", zIndex: (theme) => theme.zIndex.drawer + 1, backgroundColor: "rgba(0,0,0,0.5)" }} // Color de fondo más suave
          open={true}
        >
          <CircularProgress color="inherit" />
        </Backdrop>
      )}
    </>
  );
}

export default MainLayout;
