import React, { useState } from "react";
import { Navbar, Nav, Container, Dropdown, Image } from "react-bootstrap";
import { Link } from "react-router-dom";

function Header() {
  const pages = ["Products", "Pricing", "Blog"];
  const settings = ["Profile", "Account", "Dashboard", "Logout"];
  const [showNavDropdown, setShowNavDropdown] = useState(false);
  const [showUserDropdown, setShowUserDropdown] = useState(false);

  const handleNavDropdownToggle = () => setShowNavDropdown(!showNavDropdown);
  const handleUserDropdownToggle = () => setShowUserDropdown(!showUserDropdown);

  return (
    <Navbar bg="dark" variant="dark" expand="lg">
      <Container>
        <Navbar.Brand as={Link} to="/">
          <Image
            src="https://www.universidadesonline.cl/logos/original/logo-universidad-de-valparaiso.png"
            roundedCircle
            width={30}
            height={30}
            className="d-inline-block align-top"
            alt="Logo"
          />{' '}
          LOGO
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="navbar-nav" />
        <Navbar.Collapse id="navbar-nav">
          <Nav className="me-auto">
            {pages.map((page) => (
              <Nav.Link key={page} as={Link} to={`/${page.toLowerCase()}`}>
                {page}
              </Nav.Link>
            ))}
          </Nav>
          <Dropdown show={showUserDropdown} onToggle={handleUserDropdownToggle}>
            <Dropdown.Toggle variant="dark" id="dropdown-basic">
              <Image
                src="https://www.universidadesonline.cl/logos/original/logo-universidad-de-valparaiso.png"
                roundedCircle
                width={30}
                height={30}
                alt="User Avatar"
              />
            </Dropdown.Toggle>
            <Dropdown.Menu align="end">
              {settings.map((setting) => (
                <Dropdown.Item key={setting} as={Link} to={`/${setting.toLowerCase()}`}>
                  {setting}
                </Dropdown.Item>
              ))}
            </Dropdown.Menu>
          </Dropdown>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default Header;
