import "bootstrap/dist/css/bootstrap.min.css";
import React from "react";

const PokemonDetails = ({ data }) => {
  if (!data) return null;

  return (
    <div className="card mx-auto my-4" style={{ maxWidth: "600px" }}>
      <img
        src={data.sprites.front_default}
        className="card-img-top"
        alt={data.name}
      />
      <div className="card-body">
        <h4 className="card-title">{data.name.toUpperCase()}</h4>
        <p className="card-text">ID: {data.id}</p>
        <p className="card-text">Base Experience: {data.base_experience}</p>
        <p className="card-text">Height: {data.height}</p>
        <p className="card-text">Weight: {data.weight}</p>
        <p className="card-text">Species: {data.species.name}</p>

        <h6>Abilities:</h6>
        <ul className="list-group list-group-flush">
          {data.abilities.map((ability, index) => (
            <li key={index} className="list-group-item">
              {ability.ability.name} (Hidden: {ability.is_hidden ? "Yes" : "No"}, Slot: {ability.slot})
            </li>
          ))}
        </ul>

        <h6 className="mt-3">Stats:</h6>
        <ul className="list-group list-group-flush">
          {data.stats.map((stat, index) => (
            <li key={index} className="list-group-item">
              {stat.stat.name} (Base Stat: {stat.base_stat}, Effort: {stat.effort})
            </li>
          ))}
        </ul>

        <h6 className="mt-3">Types:</h6>
        <ul className="list-group list-group-flush">
          {data.types.map((type, index) => (
            <li key={index} className="list-group-item">
              {type.type.name}
            </li>
          ))}
        </ul>

        <h6 className="mt-3">Moves:</h6>
        <ul className="list-group list-group-flush">
          {data.moves.map((move, index) => (
            <li key={index} className="list-group-item">
              {move.move.name}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default PokemonDetails;
