import {
  Card,
  CardContent,
  CardMedia,
  List,
  ListItem,
  ListItemText,
  Typography,
} from "@mui/material";
import React from "react";

const PokemonDetails = ({ data }) => {
  if (!data) return null;

  return (
    <Card sx={{ maxWidth: 600, margin: "auto", marginTop: 4 }}>
      <CardMedia
        component="img"
        height="200"
        image={data.sprites.front_default}
        alt={data.name}
      />
      <CardContent>
        <Typography variant="h4" gutterBottom>
          {data.name.toUpperCase()}
        </Typography>
        <Typography variant="body1">ID: {data.id}</Typography>
        <Typography variant="body1">
          Base Experience: {data.base_experience}
        </Typography>
        <Typography variant="body1">Height: {data.height}</Typography>
        <Typography variant="body1">Weight: {data.weight}</Typography>
        <Typography variant="body1">Species: {data.species.name}</Typography>

        <Typography variant="h6" sx={{ marginTop: 2 }}>
          Abilities:
        </Typography>
        <List>
          {data.abilities.map((ability, index) => (
            <ListItem key={index}>
              <ListItemText
                primary={ability.ability.name}
                secondary={`Hidden: ${ability.is_hidden}, Slot: ${ability.slot}`}
              />
            </ListItem>
          ))}
        </List>

        <Typography variant="h6" sx={{ marginTop: 2 }}>
          Stats:
        </Typography>
        <List>
          {data.stats.map((stat, index) => (
            <ListItem key={index}>
              <ListItemText
                primary={stat.stat.name}
                secondary={`Base Stat: ${stat.base_stat}, Effort: ${stat.effort}`}
              />
            </ListItem>
          ))}
        </List>

        <Typography variant="h6" sx={{ marginTop: 2 }}>
          Types:
        </Typography>
        <List>
          {data.types.map((type, index) => (
            <ListItem key={index}>
              <ListItemText primary={type.type.name} />
            </ListItem>
          ))}
        </List>

        <Typography variant="h6" sx={{ marginTop: 2 }}>
          Moves:
        </Typography>
        <List>
          {data.moves.map((move, index) => (
            <ListItem key={index}>
              <ListItemText primary={move.move.name} />
            </ListItem>
          ))}
        </List>
      </CardContent>
    </Card>
  );
};

export default PokemonDetails;
