import React, { useEffect, useState } from 'react';
import PageContainer from './PageContainer';
import PokemonDetails from './PokemonDetails'; // Asegúrate de que este componente está correctamente importado

function PokemonPage() {
  const [pokemon, setPokemon] = useState(null);

  useEffect(() => {
    fetch("https://pokeapi.co/api/v2/pokemon/ditto")
      .then(response => response.json())
      .then(data => {
        setPokemon(data);
      })
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  return (
    <PageContainer title={pokemon ? pokemon.name : "Loading..."} description={`Details about ${pokemon ? pokemon.name : "the Pokémon"}`}>
      {pokemon ? <PokemonDetails data={pokemon} /> : <p>Loading...</p>}
    </PageContainer>
  );
}

export default PokemonPage;
