import React, { useState, useEffect } from 'react';
import { Box, List, ListItem, ListItemText, Typography } from '@mui/material';
import PokemonDetails from '../../../components/container/PokemonDetails';

function ListarPokemon() {
  const [pokemonList, setPokemonList] = useState([]);
  const [selectedPokemon, setSelectedPokemon] = useState(null);

  useEffect(() => {
    const fetchPokemonList = async () => {
      try {
        const response = await fetch('https://pokeapi.co/api/v2/pokemon?limit=10');
        const data = await response.json();
        setPokemonList(data.results);
      } catch (error) {
        console.error('Error fetching Pokémon list:', error);
      }
    };

    fetchPokemonList();
  }, []);

  const handlePokemonClick = async (url) => {
    try {
      const response = await fetch(url);
      const data = await response.json();
      setSelectedPokemon(data);
    } catch (error) {
      console.error('Error fetching Pokémon details:', error);
    }
  };

  return (
    <Box sx={{ padding: 2 }}>
      <Typography variant="h4" sx={{ marginBottom: 2 }}>
        Listado de Pokémon
      </Typography>
      <List>
        {pokemonList.map((pokemon) => (
          <ListItem button key={pokemon.name} onClick={() => handlePokemonClick(pokemon.url)}>
            <ListItemText primary={pokemon.name.toUpperCase()} />
          </ListItem>
        ))}
      </List>
      {selectedPokemon && <PokemonDetails data={selectedPokemon} />}
    </Box>
  );
}

export default ListarPokemon;
